import React, {useState, useEffect} from 'react'
import {
  Box,
  Card,
  Container,
  Heading,
  Stat,
  StatLabel,
  StatNumber,
  StatHelpText,
  StatArrow,
  StatGroup,
} from '@chakra-ui/react'

const EarningsExpenseInfo = () => {
  const[earnings, setEarnings] = useState([])
  const [expenses, setExpenses] = useState([])
  const [totalExpenses, setTotalExpenses] = useState(0.0)
  const [totalEarnings, setTotalEarnings] = useState(0.0)
  const [totalPercentages, setTotalPercentages] = useState(0.0)
  const [netProfits, setNetProfits] = useState(0.0)
  const [changeStatArrow, setChangeStatArrow] = useState('')
  const [error, setError] = useState(null)

  useEffect(() => {
    fetch('http://localhost:3001/api/expenseView')
      .then(response => response.json())
      .then(data => {
        const expenses = parseFloat(data.totalExpenses.toFixed(2));
        setTotalExpenses(expenses);
      })
      .catch(error => {
        setError(error);
      });

    fetch('http://localhost:3001/api/earningView')
      .then(response => response.json())
      .then(data => {
        const earnings = parseFloat(data.totalEarnings.toFixed(2));
        setTotalEarnings(earnings);
      })
      .catch(error => {
        setError(error);
      });
  }, []);

  useEffect(() => {
    const netVsLossPercentage = totalExpenses !== 0 ? ((totalEarnings - totalExpenses) / totalExpenses) * 100 : 0;
    const changeStatArrowValue = netVsLossPercentage > 0 ? 'increase' : netVsLossPercentage === 0 ? '' : 'decrease'
    const netProfit = totalEarnings - totalExpenses

    setTotalPercentages(netVsLossPercentage.toFixed(2))
    setNetProfits(netProfit.toFixed(2))
    setChangeStatArrow(changeStatArrowValue);
  }, [totalExpenses, totalEarnings]);



  return(
    <Container p={2}>
      <Card>
        <StatGroup>
          <Stat>
            <StatLabel>Expenses</StatLabel>
            <StatNumber colorScheme='red'>$ {totalExpenses}</StatNumber>
          </Stat>
          <Stat>
            <StatLabel>Earnings</StatLabel>
            <StatNumber>$ 
              {totalEarnings}
            </StatNumber>
          </Stat>
        </StatGroup>
        <StatGroup>
          <Stat>
          <StatHelpText>
            <Heading size='4xl' as='h1'>
            <StatLabel>Net</StatLabel>
              <Heading size='2xl' as='h1'>$ {netProfits}</Heading>
              <StatArrow type={changeStatArrow} />
                {totalPercentages} %
            </Heading>
            </StatHelpText>
          </Stat>
        </StatGroup>
      </Card>
    </Container>
  )
}

export default EarningsExpenseInfo
