import React, {useState} from 'react'
import coffeeCup from '../../Images/coffee-cup.jpg'
import {
  AbsoluteCenter, 
  Button, 
  ButtonGroup, 
  Center, 
  Container,
  Image,
  Input, 
  VStack
} from '@chakra-ui/react'

const UserLogin = ({setAuthenticated}) => {

  const RESPONSE_URL = process.env.REACT_APP_LOGIN_API_URL
  const [primaCredential, setPrimaCredential] = useState({
    userName: '',
    password: ''
  })
  const [message, setMessage] = useState('')

  const handleChange = (e) => {
    const { name, value } = e.target
    setPrimaCredential((prevData) => ({
      ...prevData,
      [name]: value,
    }))
  }

  const handleLogin = async (e) => {
    e.preventDefault()
    try {
      const requestBody = {
        alias: primaCredential.userName,
        password: primaCredential.password
      }

      const response = await fetch(RESPONSE_URL, {
        method: 'POST',
        headers: {
          'Content-Type' : 'application/json',
        },
        body: JSON.stringify(requestBody)
      })

      if(response.ok) {
        const data = await response.json()
        setMessage(data.message)
        setAuthenticated(true)
      } else {
        setMessage('Check again foo!')
      }

    } catch(error) {
      setMessage(`Error occured: ${error}`)
    }
  }
  return(
    <VStack>
      <Center h='100vh'>
        <Container maxW='container.sm'>
          <Image src={coffeeCup} boxSize='200px' alt='Cup of Coffee' />
          <Input type="text" name="userName" placeholder="Which prima are you?" value={primaCredential.userName} onChange={handleChange} />
          <Input type="password" name="password" placeholder="Password" value={primaCredential.password} onChange={handleChange} />
          <Button colorScheme='gray' onClick={handleLogin}>Login</Button>
        </Container>
      </Center>
    </VStack>
  )
}

export default UserLogin
