import React, {useState, useEffect} from 'react'
import {
  Box,
  Container,
  Heading,
  Stat,
  StatLabel,
  StatNumber,
  StatHelpText,
  StatArrow,
  StatGroup,
} from '@chakra-ui/react'

const EarningsExpenseInfo = () => {
  const[earnings, setEarnings] = useState([])
  const [expenses, setExpenses] = useState([])
  const [totalExpenses, setTotalExpenses] = useState(0)
  const [totalEarnings, setTotalEarnings] = useState(0)
  const [totalPercentages, setTotalPercentages] = useState(0.0)
  const [changeStatArrow, setChangeStatArrow] = useState('')
  const [error, setError] = useState(null)

  useEffect(() => {
    fetch('http://localhost:3001/api/expenseView')
      .then(response => response.json())
      .then(data => {
        const expenses = parseFloat(data.totalExpenses.toFixed(2))
        console.log(expenses)
        setTotalExpenses(expenses)

        return fetch('http://localhost:3001/api/earningView')
      })
      .then(response => response.json())
      .then(data => {
        const earnings = parseFloat(data.totalEarnings.toFixed(2))
        console.log(earnings)
        setTotalEarnings(earnings)

        const netVsLossPercentage = totalExpenses !== 0 ? ((earnings - totalExpenses) / totalExpenses) * 100 : 0
        const changeStatArrowValue = netVsLossPercentage < 0 ? 'increase' : 'decrease'

        setTotalPercentages(Math.floor(netVsLossPercentage.toFixed(0)))
        setChangeStatArrow(changeStatArrowValue)
      })
      .catch(error => {
        setError(error)
      })
  }, [])



  return(
    <Container p={2}>
      <Box>
        <StatGroup>
          <Stat>
            <StatLabel>Expenses</StatLabel>
            <StatNumber colorScheme='red'>$ {totalExpenses}</StatNumber>
          </Stat>
          <Stat>
            <StatLabel>Earnings</StatLabel>
            <StatNumber>$ 
              {totalEarnings}
            </StatNumber>
          </Stat>
        </StatGroup>
        <StatGroup>
          <Stat textAlign='center'>
          <StatHelpText>
            <Heading size='4xl' as='h1'>
              <StatArrow type={changeStatArrow} />
                {totalPercentages} %
            </Heading>
            </StatHelpText>
          </Stat>
        </StatGroup>
      </Box>
    </Container>
  )
}

export default EarningsExpenseInfo
