import React, {useState, useEffect} from 'react'
import {
  Box,
  Container,
  Heading,
  Stat,
  StatLabel,
  StatNumber,
  StatHelpText,
  StatArrow,
  StatGroup,
} from '@chakra-ui/react'

const EarningsExpenseInfo = () => {
  const[earnings, setEarnings] = useState([])
  const [expenses, setExpenses] = useState([])
  const [totalExpenses, setTotalExpenses] = useState(0)
  const [totalEarnings, setTotalEarnings] = useState(0)
  const [totalPercentages, setTotalPercentages] = useState(0.0)
  const [changeStatArrow, setChangeStatArrow] = useState('')
  const [error, setError] = useState(null)

  useEffect(() => {
    fetch('http://localhost:3001/api/expenseView')
      .then(response => response.json())
      .then(data => {
        console.log(data.totalExpenses.toFixed(2))
        setExpenses(parseFloat(data.totalExpenses.toFixed(2)))
      })
      .catch(error => {
        setError(error)
      })

    fetch('http://localhost:3001/api/earningView')
      .then(response => response.json())
      .then(data => {
        console.log(data.totalEarnings.toFixed(2))
        setEarnings(parseFloat(data.totalEarnings.toFixed(2)))
      })
      .catch(error => {
        setError(error)
      })
      const netVsLossPercentage = totalExpenses !== 0 ? ((totalEarnings - totalExpenses) / totalExpenses) * 100 : 0
      const changeStatArrow = netVsLossPercentage <= 0 ? 'increase' :
      setTotalExpenses(expenses)
      setTotalEarnings(totalEarnings)
      setTotalPercentages(Math.floor(netVsLossPercentage.toFixed(0)))
      setChangeStatArrow(changeStatArrow)
      console.log(netVsLossPercentage)
  }, [expenses, earnings])

  return(
    <Container p={2}>
      <Box>
        <StatGroup>
          <Stat>
            <StatLabel>Expenses</StatLabel>
            <StatNumber colorScheme='red'>$ {expenses}</StatNumber>
          </Stat>
          <Stat>
            <StatLabel>Earnings</StatLabel>
            <StatNumber>$ 
              {earnings}
            </StatNumber>
          </Stat>
        </StatGroup>
        <StatGroup>
          <Stat textAlign='center'>
          <StatHelpText>
            <Heading size='4xl' as='h2'>
              <StatArrow type={changeStatArrow} />
                {totalPercentages} %
            </Heading>
            </StatHelpText>
          </Stat>
        </StatGroup>
      </Box>
    </Container>
  )
}

export default EarningsExpenseInfo
