import React, {useState, useEffect} from 'react'
import {
  Container,
  Stat,
  StatLabel,
  StatNumber,
  StatHelpText,
  StatArrow,
  StatGroup,
} from '@chakra-ui/react'

const EarningsExpenseInfo = () => {
  const[earnings, setEarnings] = useState([])
  const [expenses, setExpenses] = useState([])
  const [totalExpenses, setTotalExpenses] = useState(0)
  const [totalEarnings, setTotalEarnings] = useState(0)
  const [totalPercentages, setTotalPercentages] = useState(0.0)
  const [error, setError] = useState(null)

  useEffect(() => {
    fetch('http://localhost:3001/api/expenseView')
      .then(response => response.json())
      .then(data => {
        console.log(data.totalExpenses)
        setExpenses(data.totalExpenses)
      })
      .catch(error => {
        setError(error)
      })

    fetch('http://localhost:3001/api/earningView')
      .then(response => response.json())
      .then(data => {
        console.log(data.totalEarnings)
        setEarnings(data.totalEarnings)
      })
      .catch(error => {
        setError(error)
      })
      const netVsLossPercentage = totalExpenses !== 0 ? ((totalEarnings - totalExpenses) / totalExpenses) * 100 : 0
      setTotalPercentages(netVsLossPercentage)
      console.log(netVsLossPercentage)
  }, [])

  return(
    <Container>
      <StatGroup>
        <Stat>
          <StatLabel>Expenses</StatLabel>
          <StatNumber>$ {expenses}</StatNumber>
          <StatHelpText>
            <StatArrow type='increase' />
              {totalPercentages} %
          </StatHelpText>
        </Stat>
        <Stat>
          <StatLabel>Earnings</StatLabel>
          <StatNumber>$ {earnings}</StatNumber>
          <StatHelpText>
            <StatArrow type='increase' />
              {totalPercentages} %
          </StatHelpText>
        </Stat>
      </StatGroup>
    </Container>
  )
}

export default EarningsExpenseInfo
